/* globals $, Config */

/**
 * frontend/consulta-libros/index
 */
$(document).ready(function () {
    'use strict';

    if (typeof filmotecaConfig.consultaLibro === 'undefined') {
        return;
    }

    var FIRST_LETTER_INDEX = "A";
    var LAST_LETTER_INDEX = "Z";

    var $letterSelector = $('#letter-selector');
    var $books = $('#books > .item');
    var $results = $('#results');
    var $itemsWrapper = $('.wrapper-items');
    var $modal = $('.modal');
    var $modalBody = $modal.find('.modal-content');
    var foundBooks = 0;

    /***********************
     * Slider
     ***********************/

    var filterBooks = function (event, ui) {
        foundBooks = 0;
        $books.each(function (index, book) {
            var $book = $(book);
            var awardYear = parseInt($book.data('award-year'));

            if (awardYear >= ui.values[FIRST_LETTER_INDEX] && awardYear <= ui.values[LAST_LETTER_INDEX]) {
                $book.show();
                book++;
            } else {
                $book.hide();
            }
        });

        $results.children('.number').html(foundBooks);
    };

    // // Events
    // // The user move the nuts of the letters
    $letterSelector.on('slidechange', filterBooks);

    // Initializes the default status
    $letterSelector.on('slidecreate', function () {
        filterBooks({}, {values: filmotecaConfig.consultaLibro.slider.value});
    });

    // Initialization of slider
    $letterSelector
        .slider({
            range: true,
            min : filmotecaConfig.consultaLibro.slider.minValue,
            max: filmotecaConfig.consultaLibro.slider.maxValue,
            values: filmotecaConfig.consultaLibro.slider.value,
            animate: true
        })
        .slider('pips',{
            rest: 'label',
            label: filmotecaConfig.consultaLibro.slider.label
        })
        .slider('float', {
            label: filmotecaConfig.consultaLibro.slider.label
        });

    /**************************
     * Modal
     **************************/
    var bookId;

    $modal.modal({
        show: false
    });

    $modal.on('shown.bs.modal', function () {
        $modalBody.load('/consulta-libro/' + bookId);
    });

    $itemsWrapper.on('click', '.item a', function (event) {
        var $this = $(this);

        bookId = $this.data('id');
        event.preventDefault();
        $modalBody.html('<div class="loading loading-box-md center-block"></div>');
        $modal.modal('show');
    });

    /*****************
     * Autocomple
     ******************/

    $( '#book-searcher' ).autocomplete({
        minLength: 0,
        source: '/consulta-libro',
        select: function(event, ui) {
            bookId = ui.item.id;
            $modalBody.html('<div class="loading loading-box-md center-block"></div>');
            $modal.modal('show');
            return false;
        }
    })
        .autocomplete( 'instance' )._renderItem = function(ul, item) {
        return $('<li>')
            .append(
                '<div><img src="' +item.url_image + '">' + item.name + '<br>' + item.award_date + '</div>'
            )
            .appendTo(ul);
    };
});
